import { useState } from "react"
import Switch from "react-switch"

function Title(){

    const [theme,setTheme] = useState("Light");

    function toggleSwitch(checked){
        setTheme(checked)
    }
    return(
        <header>
            <span>Mode {theme}</span>
            <Switch 
                //onChange={toggleSwitch} 
                checked = {theme}/>
        </header>
    )
}
export default Title;
