import './App.css';
import Title from './component/Title';
import Component from './component/Component';
import ProgramButton from './component/ProgramButton';
import { createContext, useState } from 'react';
import Menu from "./component/Menu";

export const ThemeContext = createContext();

function App() {

  const [theme,setTheme] = useState("light");
  const [appState,setAppState] = useState("ProgramButton")

  return (
      <ThemeContext.Provider value={{theme,setTheme,appState,setAppState}}>
        <div>
          <Title/>
          <Component/>
          {appState === "ProgramButton" &&<ProgramButton/>}
          {appState === "menu" && <Menu/>}
        </div>
      </ThemeContext.Provider>
  );
}

export default App;
